import React, { useEffect, useState } from 'react';
import axios from 'axios';
import ScheduleForm from './components/ScheduleForm';

function App() {
  const [schedules, setSchedules] = useState([]);

  const fetchSchedules = async () => {
    const res = await axios.get('http://localhost:3001/api/schedules');
    setSchedules(res.data);
  };

  useEffect(() => {
    fetchSchedules();
  }, []);

  const handleDelete = async (id) => {
    await axios.delete(`http://localhost:3001/api/schedules/${id}`);
    fetchSchedules();
  };

  return (
    <div className="App">
      <h1>Agendamentos Google Chat</h1>
      <ScheduleForm onSuccess={fetchSchedules} />
      <hr />
      <h2>Agendamentos</h2>
      <ul>
        {schedules.map(s => (
          <li key={s.id}>
            <strong>{s.texto}</strong> - {s.tipo_periodicidade} Ã s {s.hora_execucao}
            <button onClick={() => handleDelete(s.id)}>Excluir</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
